#!/usr/bin/env node

const script = require('./src/script');

script.getMergeRequestUrl()
.then(mergeRequestUrl => script.getMergeRequest(mergeRequestUrl))
.then(mergeRequest => script.getUpdateType(mergeRequest))
.then(updateType => script.increaseVersion(updateType))
.catch(err => {
	console.error(err);
	process.exit(1);
})
